package cn.poile.ucs.auth.security;import cn.poile.ucs.auth.mapper.BaseAuthMapper;import com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;import com.yzx.model.ucenter.BaseAuth;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.stereotype.Service;/** * @className: BaseUserDetails * @author: yzx * @date: 2025/8/21 6:24 * @Version: 1.0 * @description: */@Service@Slf4jpublic class UserNameUserDetailService extends BaseUserDetails {    @Autowired    private    BaseAuthMapper baseAuthMapper;    @Override    protected BaseAuth getBaseAuth(String s) throws UsernameNotFoundException {        log.debug("loadPasswordByUserName:{}", s);        BaseAuth baseAuth = baseAuthMapper.selectOne(new QueryWrapper<BaseAuth>().eq("user_name", s));        return baseAuth;    }}